function ino(){document.querySelector(".p-page-top").addEventListener("click",()=>{return 0<(document.documentElement.scrollTop||document.body.scrollTop)&&document.querySelector(".p-page-top").classList.add("floatAnime"),!1})}ino(),window.onscroll=function(){0===window.pageYOffset&&document.querySelector(".p-page-top").classList.remove("floatAnime")},"scroll load".split(" ").forEach(e=>{window.addEventListener(e,()=>{100<=window.pageYOffset?(document.querySelector(".p-page-top").classList.remove("DownMove"),document.querySelector(".p-page-top").classList.add("UpMove")):(document.querySelector(".p-page-top").classList.add("UpMove"),document.querySelector(".p-page-top").classList.add("DownMove"))})}),window.onload=function(){document.getElementById("loading").classList.add("loaded")};